pragma solidity ^0.4.24;

contract EthFundMe {
  address[] public admins;
  uint public numCampaigns;
  mapping(uint => address) campaigns;

  constructor(address[] _admins) public {
    admins = _admins;
  }

  function getNumAdmins() public view returns(uint) {
    return admins.length;
  }

  function getAdmins() public view returns(address[]) {
    return admins;
  }

  function createContract(string title, uint goal) public returns(address) {
    Campaign newCampaign = new Campaign(numCampaigns++, title, goal);
    campaigns[numCampaigns] = address(newCampaign);
    return address(newCampaign);
  }

}

contract Campaign {
  enum States {
    Pending, 
    Open, 
    Closed, 
    Rejected
  }

  struct Contribution {
    uint amount;
  }

  struct Contributor {
    address contributorAddress;
    uint totalContributed;
    mapping (uint => Contribution) contributions;
  }

  States public state;
  uint public id;
  string public title;
  uint public goal;
  address public manager;
  mapping (uint => Contributor) public contributors;
  uint public funds;

  constructor(uint _id, string _title, uint _goal) public {
    id = _id;
    title = _title;
    goal = _goal;
    manager = msg.sender;
    state = States.Pending;
  }

}
