import '../node_modules/zeppelin-solidity/contracts/access/rbac/RBAC.sol';
contract EthFundMe is RBAC, EmergencyStoppable {

  /**
   * A constant role name for indicating admins.
   */
  string public constant ROLE_ADMIN = "admin";
  uint public numAdmins = 1;

  /**
   * @dev modifier to scope access to admins
   * // reverts
   */
  modifier onlyAdmin()
  {
    checkRole(msg.sender, ROLE_ADMIN);
    _;
  }

  modifier notAdmin()
  {
    require(!hasRole(msg.sender, ROLE_ADMIN));
    _;
  }

  /**
   * @dev constructor. Sets msg.sender as admin by default
   */
  constructor() public {
    //FIXME: Can I put a require statement in a contructor? What happens if it fails?
    // require(_admins.length <= 3);

    // admins = _admins;
    // for (uint i = 0; i < admins.length; i++) {
    //   isAdmin[admins[i]] = true;
    // }

    addRole(msg.sender, ROLE_ADMIN);
  }

  /**
   * @dev add a role to an account
   * @param _account the account that will have the role
   * @param _roleName the name of the role
   */
  function adminAddRole(address _account, string _roleName)
    public
    onlyAdmin
  {
    addRole(_account, _roleName);
    numAdmins++;
  }

  /**
   * @dev remove a role from an account
   * @param _account the account that will no longer have the role
   * @param _roleName the name of the role
   */
  function adminRemoveRole(address _account, string _roleName)
    public
    onlyAdmin
  {
    removeRole(_account, _roleName);
    numAdmins--;
  }


// contract EthFundMe is EmergencyStoppable {

